    public HtmlSanitizer Sanitizer { get; set; }

    public HtmlSanitizerTests(HtmlSanitizerFixture fixture)
    {
        Encoding.RegisterProvider(CodePagesEncodingProvider.Instance);
        Sanitizer = fixture.Sanitizer;
