        html = @"<DIV STYLE='background-image: \000075 rl(javascript:alert(""foo""))'>";
        Assert.Equal(@"<div></div>", sanitizer.Sanitize(html), ignoreCase: true);
        html = @"<DIV STYLE='background-image: \000075
rl(javascript:alert(""foo""))'>";
        Assert.Equal(@"<div></div>", sanitizer.Sanitize(html), ignoreCase: true);
    }

